mapper.select:

	and(
			member_no = #{userNo}
		 or (
				inter_appro = '대기' 
       		and inter_no = #{userNo} 
        	 or inter_appro = '결재'	
			and final_no = #{userNo}
			)
		or  (
				    inter_appro = '대기' 
			    and final_no = #{userNo}	
			)
		)


		and (
				inter_appro = '대기' 
       		and inter_no = #{userNo} 
        	 or inter_appro = '결재'	
			and final_no = #{userNo}
			)


and ( 	
	member_no = #{userNo}	
or
	(
	   inter_no = #{userNo} 
	or final_no = #{userNo}
	)
)



	and(
			a.member_no = #{userNo}

		or (
				inter_appro = '대기' 
       		and inter_no = #{userNo} 
        	 or inter_appro = '결재'	
			and final_no = #{userNo}
			)

		or  (
			    inter_appro = '대기' 
		    and final_no = #{userNo}	
			)
		)


		and (
				inter_appro = '대기' 
       		and inter_no = #{userNo} 
        	 or inter_appro = '결재'	
			and final_no = #{userNo}
			)


and ( 	
		a.member_no = #{userNo}	
	or
		(
		   inter_no = #{userNo} 
		or final_no = #{userNo}
		)
	)

mapper.update:
update tb_approval
   set inter_appro = #{interAppro},
       final_appro = #{finalAppro}
 where appro_no = #{approNo}

mapper.insert:
   		insert
   		  into tb_approval
   		  	   (
   		  	   	  appro_no
   		  	   	, member_no
   		  	   	, tpl_no
   		  	   	, appro_title
   		  	   	, appro_content
   		  	   	, inter_no
   		  	   	, final_no
   		  	   	, create_date  
   		  	   	, inter_appro
   		  	   	, final_appro
		  	   )
		values (
				  SEQ_APPRONO.NEXTVAL
				, #{writerNo}
				, #{tplNo}
				, #{approTitle}
				, #{approContent}
				, #{interNo}
				, #{finalNo}
				, to_char(sysdate,'YYYY-MM-DD')
				, '대기'
				, '대기'
				)
